#!/bin/bash
if [ x$1=x"precustomization" ];
then

mkdir -p /home/ubuntu/.ssh

echo 'ssh-rsa  foo@bar' >> /home/ubuntu/.ssh/authorized_keys
echo 'ssh-rsa  manager' >> /home/ubuntu/.ssh/authorized_keys

chown ubuntu.ubuntu /home/ubuntu/.ssh
chown ubuntu.ubuntu /home/ubuntu/.ssh/authorized_keys
chmod go-rwx /home/ubuntu/.ssh
chmod go-rwx /home/ubuntu/.ssh/authorized_keys

fi
